mapper.select:
select 

,

from cr_review
where reviewer = #{reviewer,jdbcType=INTEGER}
  and submission = #{submission,jdbcType=INTEGER}

mapper.delete:
delete from cr_review
where reviewer = #{reviewer,jdbcType=INTEGER}
  and submission = #{submission,jdbcType=INTEGER}

mapper.update:
update cr_review
set initial_score = #{initialScore,jdbcType=INTEGER},
  confidence = #{confidence,jdbcType=INTEGER},
  final_score = #{finalScore,jdbcType=INTEGER},
  initial_comment = #{initialComment,jdbcType=LONGVARCHAR},
  rebuttal = #{rebuttal,jdbcType=LONGVARCHAR},
  final_comment = #{finalComment,jdbcType=LONGVARCHAR}
where reviewer = #{reviewer,jdbcType=INTEGER}
  and submission = #{submission,jdbcType=INTEGER}

update cr_review
set initial_score = #{initialScore,jdbcType=INTEGER},
  confidence = #{confidence,jdbcType=INTEGER},
  final_score = #{finalScore,jdbcType=INTEGER}
where reviewer = #{reviewer,jdbcType=INTEGER}
  and submission = #{submission,jdbcType=INTEGER}
